import urllib.parse
import base64

encoded_path = "vscode-remote://dev-.ipynb"

# Remove 'vscode-remote://dev-container+' from the beginning
encoded_path = encoded_path.replace('vscode-remote://dev-container+', '', 1)

# URL decode
url_decoded_path = urllib.parse.unquote(encoded_path)

# Base64 decode (using 'utf-8' instead of 'ascii')
base64_bytes = url_decoded_path.encode('ascii')
decoded_bytes = base64.b64decode(base64_bytes)
decoded_path = decoded_bytes.decode('utf-8')  # Use 'utf-8' here

print(decoded_path)